import React, { useState, useRef, useEffect } from 'react';
import Editor from '@monaco-editor/react';
import './App.css';

function App() {
  const [code, setCode] = useState('');
  const [language, setLanguage] = useState('javascript');
  const [fileName, setFileName] = useState('untitled.js');
  const [showOpenMenu, setShowOpenMenu] = useState(false);
  const [showFileExplorer, setShowFileExplorer] = useState(false);
  const [showTerminal, setShowTerminal] = useState(false);
  const [terminalOutput, setTerminalOutput] = useState('');
  const [terminalInput, setTerminalInput] = useState('');
  const [fileExplorerPath, setFileExplorerPath] = useState('/');
  const [fileExplorerFiles, setFileExplorerFiles] = useState([]);
  const [fileExplorerLoading, setFileExplorerLoading] = useState(false);
  const fileInputRef = useRef(null);
  const dropdownRef = useRef(null);
  const terminalRef = useRef(null);
  const fileExplorerRef = useRef(null);

  // Dropdown dƒ±≈üƒ±na tƒ±klandƒ±ƒüƒ±nda kapat
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setShowOpenMenu(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  // Dosya uzantƒ±sƒ±na g√∂re dil se√ßimi
  const getLanguageFromFileName = (fileName) => {
    const extension = fileName.split('.').pop().toLowerCase();
    const languageMap = {
      'js': 'javascript',
      'jsx': 'javascript',
      'ts': 'typescript',
      'tsx': 'typescript',
      'html': 'html',
      'css': 'css',
      'scss': 'scss',
      'json': 'json',
      'py': 'python',
      'java': 'java',
      'cpp': 'cpp',
      'c': 'c',
      'php': 'php',
      'rb': 'ruby',
      'go': 'go',
      'rs': 'rust',
      'sql': 'sql',
      'md': 'markdown',
      'xml': 'xml',
      'yaml': 'yaml',
      'yml': 'yaml'
    };
    return languageMap[extension] || 'plaintext';
  };

  // Yeni dosya olu≈ütur
  const handleNewFile = () => {
    setCode('');
    setFileName('untitled.js');
    setLanguage('javascript');
  };

  // Dosya a√ß
  const handleOpenFile = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        setCode(e.target.result);
        setFileName(file.name);
        setLanguage(getLanguageFromFileName(file.name));
        setShowOpenMenu(false);
      };
      reader.readAsText(file);
    }
  };

