import React, { useState, useRef } from 'react';
import Editor from '@monaco-editor/react';
import './App.css';

function App() {
  const [code, setCode] = useState('');
  const [language, setLanguage] = useState('javascript');
  const [fileName, setFileName] = useState('untitled.js');
  const [showOpenMenu, setShowOpenMenu] = useState(false);
  const fileInputRef = useRef(null);

  // Dosya uzantısına göre dil seçimi
  const getLanguageFromFileName = (fileName) => {
    const extension = fileName.split('.').pop().toLowerCase();
    const languageMap = {
      'js': 'javascript',
      'jsx': 'javascript',
      'ts': 'typescript',
      'tsx': 'typescript',
      'html': 'html',
      'css': 'css',
      'scss': 'scss',
      'json': 'json',
      'py': 'python',
      'java': 'java',
      'cpp': 'cpp',
      'c': 'c',
      'php': 'php',
      'rb': 'ruby',
      'go': 'go',
      'rs': 'rust',
      'sql': 'sql',
      'md': 'markdown',
      'xml': 'xml',
      'yaml': 'yaml',
      'yml': 'yaml'
    };
    return languageMap[extension] || 'plaintext';
  };

  // Yeni dosya oluştur
  const handleNewFile = () => {
    setCode('');
    setFileName('untitled.js');
    setLanguage('javascript');
  };

  // Dosya aç
  const handleOpenFile = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        setCode(e.target.result);
        setFileName(file.name);
        setLanguage(getLanguageFromFileName(file.name));
      };
      reader.readAsText(file);
    }
  };

  // Dosya kaydet
  const handleSaveFile = () => {
    const blob = new Blob([code], { type: 'text/plain' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = fileName;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  // Dosya adı değiştiğinde dil otomatik güncelle
  const handleFileNameChange = (newFileName) => {
    setFileName(newFileName);
    setLanguage(getLanguageFromFileName(newFileName));
  };

  return (
    <div className="app">
      <div className="toolbar">
        <div className="menu">
          <button onClick={handleNewFile} className="menu-button">
            Yeni
          </button>
          <button onClick={() => fileInputRef.current.click()} className="menu-button">
            Aç
          </button>
          <button onClick={handleSaveFile} className="menu-button">
            Kaydet
          </button>
        </div>
        <div className="file-info">
          <input
            type="text"
            value={fileName}
            onChange={(e) => handleFileNameChange(e.target.value)}
            className="file-name-input"
            placeholder="Dosya adı"
          />
          <span className="language-badge">{language}</span>
        </div>
        <input
          type="file"
          ref={fileInputRef}
          onChange={handleOpenFile}
          style={{ display: 'none' }}
          accept=".js,.jsx,.ts,.tsx,.html,.css,.scss,.json,.py,.java,.cpp,.c,.php,.rb,.go,.rs,.sql,.md,.xml,.yaml,.yml,.txt"
        />
      </div>
      <div className="editor-container">
        <Editor
          height="calc(100vh - 60px)"
          defaultLanguage="javascript"
          language={language}
          value={code}
          onChange={(value) => setCode(value || '')}
          theme="vs-dark"
          options={{
            minimap: { enabled: true },
            fontSize: 14,
            wordWrap: 'on',
            automaticLayout: true,
            scrollBeyondLastLine: false,
            roundedSelection: false,
            readOnly: false,
            cursorStyle: 'line',
            scrollbar: {
              vertical: 'visible',
              horizontal: 'visible'
            }
          }}
        />
      </div>
    </div>
  );
}

export default App;
