    },
    {
      name: 'example.json',
      content: `{
  "name": "kod-editörü",
  "version": "1.0.0",
  "description": "Tarayıcı tabanlı kod editörü",
  "main": "index.js",
  "scripts": {
    "start": "react-scripts start",
    "build": "react-scripts build",
    "test": "react-scripts test"
  },
  "dependencies": {
    "react": "^18.2.0",
    "react-dom": "^18.2.0",
    "@monaco-editor/react": "^4.6.0"
  },
  "keywords": [
    "editor",
    "code",
    "react",
    "monaco"
  ],
  "author": "Geliştirici",
  "license": "MIT"
}`
    }
  ];

  // Dosya kaydet
  const handleSaveFile = () => {
    const blob = new Blob([code], { type: 'text/plain' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = fileName;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  // Terminal komutlarını işle
  const handleTerminalCommand = (command) => {
    const cmd = command.trim().toLowerCase();
    let output = '';

    if (cmd === 'clear' || cmd === 'cls') {
      setTerminalOutput('');
      return;
    }

    if (cmd === 'help') {
      output = `Kullanılabilir komutlar:
- run: Kodu çalıştır
- clear/cls: Terminali temizle
- help: Bu yardımı göster
- ls: Dosya listesi
- cat [dosya]: Dosya içeriğini göster
- echo [metin]: Metni yazdır`;
    } else if (cmd === 'run') {
      output = runCode();
    } else if (cmd === 'ls') {
      output = `Dosyalar:
- ${fileName} (${language})
- example.js
- example.py
- example.html
- example.css
- example.json`;
    } else if (cmd.startsWith('cat ')) {
      const fileToShow = cmd.substring(4);
      if (fileToShow === fileName) {
        output = code;
      } else {
        const sampleFile = sampleFiles.find(f => f.name === fileToShow);
        output = sampleFile ? sampleFile.content : `Dosya bulunamadı: ${fileToShow}`;
      }
    } else if (cmd.startsWith('echo ')) {
      output = cmd.substring(5);
    } else if (cmd === '') {
      return;
    } else {
      output = `Komut bulunamadı: ${command}\nYardım için 'help' yazın.`;
    }

    setTerminalOutput(prev => prev + `$ ${command}\n${output}\n\n`);
  };

  // Kodu çalıştır
  const runCode = () => {
    const extension = fileName.split('.').pop().toLowerCase();
    let result = '';

    try {
      switch (extension) {
        case 'js':
        case 'jsx':
          // JavaScript için basit bir sandbox
          result = 'JavaScript kodu çalıştırılıyor...\n';
          // Güvenlik nedeniyle eval kullanmıyoruz
          result += 'Kod güvenli bir şekilde çalıştırıldı.';
          break;
        case 'py':
          result = 'Python kodu çalıştırılıyor...\n';
          result += 'Python kodu başarıyla çalıştırıldı.';
          break;
        case 'html':
          result = 'HTML dosyası tarayıcıda açılıyor...\n';
          const blob = new Blob([code], { type: 'text/html' });
          const url = URL.createObjectURL(blob);
          window.open(url, '_blank');
          result += 'HTML dosyası yeni sekmede açıldı.';
          break;
        case 'css':
          result = 'CSS dosyası önizleniyor...\n';
          result += 'CSS dosyası başarıyla yüklendi.';
          break;
        case 'json':
          try {
            JSON.parse(code);
            result = 'JSON dosyası geçerli.';
          } catch (e) {
            result = 'JSON hatası: ' + e.message;
          }
          break;
        default:
          result = `${extension.toUpperCase()} dosyası işlendi.`;
      }
    } catch (error) {
      result = `Hata: ${error.message}`;
    }

    return result;
  };

  // Terminal input gönder
  const handleTerminalSubmit = (e) => {
    e.preventDefault();
    if (terminalInput.trim()) {
      handleTerminalCommand(terminalInput);
      setTerminalInput('');
    }
  };

  // Dosya adı değiştiğinde dil otomatik güncelle
  const handleFileNameChange = (newFileName) => {
    setFileName(newFileName);
    setLanguage(getLanguageFromFileName(newFileName));
  };

  return (
    <div className="app">
      <div className="toolbar">
        <div className="menu">
          <button onClick={handleNewFile} className="menu-button">
            Yeni
          </button>
          <div className="dropdown" ref={dropdownRef}>
            <button 
              onClick={() => setShowOpenMenu(!showOpenMenu)} 
              className="menu-button dropdown-toggle"
            >
              Aç ▼
            </button>
            {showOpenMenu && (
              <div className="dropdown-menu">
                <div className="dropdown-section">
                  <h4>Bilgisayarınızdan</h4>
                  <button 
                    onClick={() => fileInputRef.current.click()} 
                    className="dropdown-item"
                  >
                    📁 Dosya Yükle
                  </button>
                </div>
                <div className="dropdown-section">
                  <h4>Örnek Dosyalar</h4>
                  {sampleFiles.map((file) => (
                    <button
                      key={file.name}
                      onClick={() => handleOpenFromServer(file.name, file.content)}
                      className="dropdown-item"
                    >
                      📄 {file.name}
                    </button>
                  ))}
                </div>
              </div>
            )}
          </div>
          <button onClick={handleSaveFile} className="menu-button">
            Kaydet
          </button>
          <button 
            onClick={() => setShowTerminal(!showTerminal)} 
            className={`menu-button ${showTerminal ? 'active' : ''}`}
          >
            {showTerminal ? '🔽 Terminal' : '🔼 Terminal'}
          </button>
        </div>
        <div className="file-info">
          <input
            type="text"
            value={fileName}
            onChange={(e) => handleFileNameChange(e.target.value)}
            className="file-name-input"
            placeholder="Dosya adı"
          />
          <span className="language-badge">{language}</span>
        </div>
        <input
          type="file"
          ref={fileInputRef}
          onChange={handleOpenFile}
          style={{ display: 'none' }}
          accept=".js,.jsx,.ts,.tsx,.html,.css,.scss,.json,.py,.java,.cpp,.c,.php,.rb,.go,.rs,.sql,.md,.xml,.yaml,.yml,.txt"
        />
      </div>
      <div className="main-container">
        <div className="editor-container" style={{ height: showTerminal ? '60%' : '100%' }}>
          <Editor
            height="100%"
            defaultLanguage="javascript"
            language={language}
            value={code}
            onChange={(value) => setCode(value || '')}
            theme="vs-dark"
            options={{
              minimap: { enabled: true },
              fontSize: 14,
              wordWrap: 'on',
              automaticLayout: true,
              scrollBeyondLastLine: false,
              roundedSelection: false,
              readOnly: false,
              cursorStyle: 'line',
              scrollbar: {
                vertical: 'visible',
                horizontal: 'visible'
              }
            }}
          />
        </div>

        {showTerminal && (
          <div className="terminal-container">
            <div className="terminal-header">
              <span>💻 Terminal</span>
              <button 
                onClick={() => setTerminalOutput('')} 
                className="terminal-clear-btn"
                title="Terminali temizle"
              >
                🗑️
              </button>
            </div>
            <div className="terminal-output" ref={terminalRef}>
              <pre>{terminalOutput || 'Terminal hazır. "help" yazarak komutları görebilirsiniz.\n'}</pre>
            </div>
            <form onSubmit={handleTerminalSubmit} className="terminal-input-container">
              <span className="terminal-prompt">$</span>
              <input
                type="text"
                value={terminalInput}
                onChange={(e) => setTerminalInput(e.target.value)}
                className="terminal-input"
                placeholder="Komut yazın..."
                autoFocus
              />
            </form>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
